        from __status _s
        where c.cluster_identifier = _s.cluster_identifier
    ;

    with __ignore as (
      select _pi.*
        from jsonb_array_elements( args->'ignores' ) e( doc )
          inner join jsonb_populate_record( null::cluster.ignore, e.doc ) _pi on true
