CREATE FUNCTION cluster.sets_cluster_license(args jsonb) RETURNS SETOF cluster.cluster
    LANGUAGE plpgsql
    AS $$
declare
  /**
    args := {
      cluster_uid:UID
      cluster_licenselive: NUMBE >> MENSAL=>1, ANUAL => 2
      cluster_licenserepeate: NUMBER >> REPITICAO DO PERIODO
    }
   */
  _year int default 0;
  _month int default 0;
  _cluster cluster.cluster;
begin
  _cluster := jsonb_populate_record( _cluster, args );

  if _cluster.cluster_licenselife is not null
    and _cluster.cluster_licenserepeate is not null
    and _cluster.cluster_licenserepeate > 0
  then
    _year := coalesce( case when _cluster.cluster_licenselife = 1 then 1 end, 0 );
    _month := coalesce( case when _cluster.cluster_licenselife = 1 then 1 end, 0 );
    _cluster.cluster_license := current_timestamp
      + make_interval( (_year * _cluster.cluster_licenserepeate)::int, (_month* _cluster.cluster_licenserepeate)::int );

    return query
      select ( "returning" ).*
        from lib.sets( _cluster );
  end if;
end;
$$;


