--
-- TOC entry 695 (class 1255 OID 1562192)
-- Name: combinator(text[], integer, text[], integer[], integer); Type: FUNCTION; Schema: lib; Owner: -
--

CREATE FUNCTION lib.combinator(items text[], start integer DEFAULT 1, parent text[] DEFAULT NULL::text[], indexs integer[] DEFAULT NULL::integer[], size integer DEFAULT NULL::integer) RETURNS TABLE(combination_items text[], combination_elements integer, combination_indexs integer[])
    LANGUAGE plpgsql IMMUTABLE
    AS $$
declare

    /**
      Essa funo serve para combinar de uma forma unica os elementos de uma lista
      items corresponde aos elementos que devem ser combinados ( arg_element_items )
      start corresponde a possio a qual a combinao devera iniciar (arg_element_start)
      parent cooresponde aos elementos j combinado que as novas combinaes devero conter na raiz (arg_element_parent)
      indexs corresponde ao index de todos os elementos j combinados no parrente (arg_element_index)
      size corresponde ao tamanho do array dos elementos que devero ser combinadoos (arg_element_sizes)
     */

    arg_complete text[];
    arg_index int[];
    arg_items_count int default array_length( parent, 1 );
  begin

    -- Quando a lista dos itens a se combinar for nulo ou vazia ento deve-se sair das tentativas de combinao
    if items is null or array_length( items, 1 ) = 0 then
      return;
    end if;

    -- Obter o tamanho total da lista dos elementos
    if size is null then
      size := array_length( items, 1 );
    end if;

    arg_items_count := coalesce( arg_items_count, 0 );
    start := coalesce( start, 1 );

    -- Combinar o parrente com todos os item apartir da posio atual do array
    for i in start .. size loop

      if parent is null then
        arg_complete := array [ items[ i ] ];
        arg_index := array [ i ];
      else
        arg_complete := parent || items[ i ];
        arg_index := indexs || i;
      end if;

      -- Para a nova combinao encontrada, combitar com os proximos item da lista caso tiver mais item na lista
      if i < size then
        return query
          select *
            from lib.combinator( items, i+1, arg_complete, arg_index, size )
        ;
      end if;

      return query select arg_complete, arg_items_count +1, arg_index;

    end loop;
  end;
$$;


