  return query
    with artigo_item as (
      select
        a.*,
        art.*,
        st.stock_id,
        st.stock_quantidade,
        jsonb_agg(
          lib.jsonb_values_as(
            to_jsonb( it ),
            'artigo_id', 'item_id',
