  with posto_cheques as (
    select
        cx.caixa_posto_id,
        count( ct.conta_id ) filter( where ct.conta_tpaga_id = _const.tpaga_cheque ) as conta_cheque
      from tweeks.conta ct
        inner join tweeks.caixa cx on ct.conta_caixa_fechopagamento = cx.caixa_id
