CREATE FUNCTION tweeks.repcolumns(args jsonb) RETURNS SETOF jsonb
    LANGUAGE plpgsql
    AS $$
declare
  /**
    args: { "source":"REPORT_SOURCE" }
   */
  _src character varying default args->>'source';
  _source record;
begin
  select * into _source
    from tweeks.report_source( _src );

  -- COLUMNS
  return query
    select
      jsonb_build_object(
        'column', rc.name,
        'name', coalesce( rc.rename, rc.name ),
        'type', 'column',
        'format', rc.format,
        'init', rc.init
      )
    from tweeks.repcolumn rc
    where rc.source = _source.source_format
      and rc.show
    order by rc.position desc
      nulls last,
      coalesce( rc.rename, rc.name )
  ;

  -- FILTER
  return query
    with __filters as (
      select rc.*, tweeks.reptemplateof( f.doc, to_jsonb( rc ) || to_jsonb( _source ) || to_jsonb( r ) ) as doc
      from tweeks.repcolumn rc
        inner join tweeks.report r on rc.source = r.report_source
        inner join jsonb_array_elements( rc.filter ) f( doc ) on true
      where rc.source = _source.source_format
        and jsonb_array_length( rc.filter ) > 0
    ) select jsonb_build_object(
      'opr', f.doc->'opr',
      'column', f.name,
      'name', f.doc->'name',
      'mode', f.doc->'mode',
      'source', f.doc->'source',
      'src', f.doc->'src',
      'format', f.doc->'format',
      'type', 'filter'
    ) from __filters  f
    order by
      coalesce( ( f.doc->>'position')::int2, f.position ) desc
      nulls last,
      coalesce( f.doc->>'name', f.name )
  ;

  -- GROUP
  return query
    with __filters as (
      select rc.*, tweeks.reptemplateof( f.doc, to_jsonb( rc ) || to_jsonb( _source ) ) as doc
      from tweeks.repcolumn rc
        inner join jsonb_array_elements( rc.agg ) f( doc ) on true
      where rc.source = _source.source_format
        and jsonb_array_length( rc.agg ) > 0
    ) select jsonb_build_object(
      'column', f.name,
      'name', f.doc -> 'name',
      'func', f.doc -> 'func',
      'format', f.doc -> 'format',
      'rename', coalesce( f.doc->>'rename', f.doc->>'name', f.name ),
      'type', 'group',
      'position', to_jsonb( coalesce( ( f.doc->>'position')::int2, f.position ) ),
      'init', coalesce( f.doc->'init', 'false'::jsonb )
    ) from __filters  f
    order by
      coalesce( ( f.doc->>'position')::int2, f.position ) desc
      nulls last,
      coalesce( f.doc->>'rename', f.doc->>'name', f.name )
  ;
end;
$$;


