CREATE FUNCTION tweeks.report_prepare(report jsonb) RETURNS SETOF text
    LANGUAGE plpgsql
    AS $_$
declare
  /**
    report := {
      source: view|table
      _branch_uid: BRANCH-UID
      columns: [{
        column:COLUMN,
        order:NULL|ASC|DESC
      }]
      filters: [{
        column: src.COL-NAME,
        opr: =|<|>|<=|>=|like
        mode?: default|right
        value: VALUE-FILTER
      }],
      groups:[{
        column: src.COL-NAME
        func: count|sum|max|min|avg
        rename: NEW-NAME
      }]
    }
   */
  _columns record;
  _grp record;
  _statement text;
  _whr record;
  _source record;
begin

  select * into _source
    from tweeks.report_source( (report->>'source' )::regclass )
  ;

  with __source_columns as(
    select
      col.column_name,
      format_type( pt.oid, atttypmod ) column_type,
      col.data_type = 'ARRAY' as is_array,
      col.column_default,
      col.is_generated = 'ALWAYS' as is_generated
    from information_schema.columns col
           inner join tweeks.report_source( _source.source_class ) c on c.source_name = col.table_name
      and col.table_schema::regnamespace = c.source_schema::regnamespace
           inner join pg_attribute att on col.column_name = att.attname
      and att.attrelid = format( '%s.%s', col.table_schema, col.table_name )::regclass::oid
           inner join pg_type pt on att.atttypid = pt.oid
    where format('%s.%s', c.source_schema, c.source_name)::regclass = _source.source_class
  ), __columns as (
    select
        e.doc->>'column' as column_name,
        e.doc->>'order' as column_order,
        e.ordinality
      from jsonb_array_elements( report->'columns' ) with ordinality e( doc )
  ), __prepare_columns as (
    select
        format( '%I', sc.column_name ) as  column_name,
        case
          when lower( c.column_order ) in( 'asc', 'desc') then lower( c.column_order )
        end  as column_order,
        c.ordinality
      from __source_columns sc
        inner join __columns c on sc.column_name = c.column_name
  ) select
      count( pc.column_name ) as shows,
      count( pc.column_order ) filter ( where pc.column_order is not null ) as order_counts,
      string_agg( format( '%s %s', pc.column_name, pc.column_order ), ', ' order by pc.ordinality ) filter ( where pc.column_order is not null ) as orders,
      string_agg( pc.column_name, ', ' order by pc.ordinality ) as columns
      into _columns
    from __prepare_columns pc
;

  with __source_columns as(
    select
      col.column_name,
      format_type( pt.oid, atttypmod ) column_type,
      case
        when col.data_type = 'ARRAY' then format_type( pt.typelem, atttypmod )
        else format_type( pt.oid, atttypmod )
      end as column_basetype,
      col.data_type = 'ARRAY' as is_array,
      col.column_default,
      col.is_generated = 'ALWAYS' as is_generated
    from information_schema.columns col
           inner join tweeks.report_source( _source.source_class ) c on c.source_name = col.table_name
      and col.table_schema::regnamespace = c.source_schema::regnamespace
           inner join pg_attribute att on col.column_name = att.attname
      and att.attrelid = format( '%s.%s', col.table_schema, col.table_name )::regclass::oid
           inner join pg_type pt on att.atttypid = pt.oid
    where format('%s.%s', c.source_schema, c.source_name)::regclass = _source.source_class
  ), __filters as (
    select
        e.doc->>'column' as filter_column,
        coalesce( e.doc->>'opr', '=' ) as filter_opr,
        coalesce( e.doc->>'mode', 'default' ) as filter_oprmode,
        e.doc->>'value' as filter_value
      from jsonb_array_elements( report->'filters' ) e( doc )
  ), __prepare_filter as (
    select
        _f.filter_column,
        _f.filter_opr,
        _f.filter_oprmode,
        case
          when lower( _f.filter_opr ) = 'like' and _f.filter_oprmode = 'default' then format( '%s%s%s', '%', lower( _f.filter_value ), '%' )
          when lower( _f.filter_opr ) = 'like' and _f.filter_oprmode = 'right' then format( '%s%s', lower( _f.filter_value ), '%')
          else filter_value
        end as filter_value
      from __filters _f
  ), __prepare_where as (
    select
        case
          when _sc.is_array then format( 'any( %I )', _sc.column_name)
          when _f.filter_opr = 'like' then format( 'lower( %I )::text', _sc.column_name )
          else format( '%I', _sc.column_name )
        end as column_exp,

        case
          when _sc.is_array then format( '(%L)::%s', _f.filter_value, _sc.column_basetype )
          when _f.filter_opr = 'like' then format( '(%L)::text', _f.filter_value )
          else format( '(%L)::%s', _f.filter_value, _sc.column_type )
        end as args,
        _sc.*,
        _f.*

      from __source_columns _sc
        inner join __prepare_filter _f on _sc.column_name = _f.filter_column
  ) select
        count( * ) as apply,
        string_agg(
          case
            when  _pr.is_array then format( '%s %s %s', args, filter_opr, column_exp )
            else format( '%s %s %s', column_exp, filter_opr, args )
          end,' and '

        ) as filter_exp
        into _whr
      from __prepare_where _pr
  ;

  with __source_columns as(
    select
      col.column_name,
      format_type( pt.oid, atttypmod ) column_type,
      col.data_type = 'ARRAY' as is_array,
      col.column_default,
      col.is_generated = 'ALWAYS' as is_generated
    from information_schema.columns col
      inner join tweeks.report_source( _source.source_class ) c on c.source_name = col.table_name
        and col.table_schema::regnamespace = c.source_schema::regnamespace
      inner join pg_attribute att on col.column_name = att.attname
        and att.attrelid = format( '%s.%s', col.table_schema, col.table_name )::regclass::oid
      inner join pg_type pt on att.atttypid = pt.oid
    where format('%s.%s', c.source_schema, c.source_name)::regclass = _source.source_class
  ), __group as (
    select
        e.doc->>'column' as group_column,
        e.doc->>'mode' as group_mode,
        e.doc->>'func' as group_func,
        e.doc->>'rename' as group_rename
      from jsonb_array_elements( report->'groups' ) e( doc )
  ), __prepare_group as (
    select
        g.group_column,
        case
          when g.group_mode in ( 'default', 'distinct' ) then g.group_mode
