CREATE FUNCTION tweeks.report_temporal(anyelement, representation text) RETURNS TABLE(generate text, value anyelement)
    LANGUAGE plpgsql
    AS $_$
  declare
  begin
    if pg_typeof( $1 ) = 'date'::regtype then
      generate = to_char( $1, $2 );
      value := to_date( generate, $2 );
      return next;
    elsif pg_typeof( $1 ) in ( 'timetz'::regtype, 'time'::regtype ) then
      generate = to_char( $1::timestamp, $2 );
